sp_widget.script:
(function () {

  data.reports = [];

  // ðŸ”¹ TOP SUMMARY COUNTERS
  data.activeCourses = 0;
  data.totalRegistered = 0;
  data.totalWaitlisted = 0;

  var courseGR = new GlideRecord('x_1862243_training_training_course_table');
  courseGR.addQuery('instructor', gs.getUserID());
  courseGR.query();

  while (courseGR.next()) {

    // âœ… Count active courses
    if (courseGR.is_active) {
      data.activeCourses++;
    }

    // ðŸ”¹ Registered count
    var regAgg = new GlideAggregate('x_1862243_training_training_registration_table');
    regAgg.addQuery('course', courseGR.sys_id);
    regAgg.addQuery('registration_status', 'registered');
    regAgg.addAggregate('COUNT');
    regAgg.query();
    regAgg.next();
    var registered = parseInt(regAgg.getAggregate('COUNT'), 10) || 0;

    // ðŸ”¹ Waitlisted count
    var waitAgg = new GlideAggregate('x_1862243_training_training_registration_table');
    waitAgg.addQuery('course', courseGR.sys_id);
    waitAgg.addQuery('registration_status', 'waitlisted');
    waitAgg.addAggregate('COUNT');
    waitAgg.query();
    waitAgg.next();
    var waitlisted = parseInt(waitAgg.getAggregate('COUNT'), 10) || 0;

    // âœ… Accumulate instructor totals
    data.totalRegistered += registered;
    data.totalWaitlisted += waitlisted;

    var maxSeats = parseInt(courseGR.maximum_seats, 10) || 0;
    var availableSeats = parseInt(courseGR.available_seats, 10) || 0;

    var filledPercent = maxSeats > 0
      ? Math.round(((maxSeats - availableSeats) / maxSeats) * 100)
      : 0;

    data.reports.push({
      course: courseGR.course_name.toString(),
      status: courseGR.is_active ? 'Active' : 'Inactive',
      maxSeats: maxSeats,
      availableSeats: availableSeats,
      registered: registered,
      waitlisted: waitlisted,
      filled: filledPercent + '%',
      endDate: courseGR.getDisplayValue('course_end_date')
    });
  }

})();
