sp_widget.script:
(function () {

  data.course = {};
  data.learners = [];

  var courseId = $sp.getParameter('course_id');
  if (!courseId) return;

  // Security check (important)
  if (!gs.hasRole('x_1862243_training.training_instructor')) {
    return;
  }

  var courseGR = new GlideRecord('x_1862243_training_training_course_table');
  if (courseGR.get(courseId)) {

    data.course = {
      name: courseGR.course_name.toString(),
      status: courseGR.is_active ? 'Active' : 'Inactive',
      maxSeats: courseGR.maximum_seats.toString(),
      availableSeats: courseGR.available_seats.toString(),
      startDate: courseGR.getDisplayValue('course_start_date'),
      endDate: courseGR.getDisplayValue('course_end_date')
    };
  }

})();
