sp_widget.client_script:
api.controller = function($scope, $interval) {
  var c = this;

  function updateClock() {
    var now = new Date();
    var hours = now.getHours();

    // 1. Greeting & Classes
    if (hours >= 5 && hours < 12) {
      c.greeting = "Good morning";
      c.timeClass = "morning-icon";
    } else if (hours >= 12 && hours < 17) {
      c.greeting = "Good afternoon";
      c.timeClass = "afternoon-icon";
    } else {
      c.greeting = "Good evening";
      c.timeClass = "evening-icon";
    }

    // 2. Formatting Time
    c.displayTime = now.toLocaleTimeString([], { 
      hour: '2-digit', 
      minute: '2-digit', 
      hour12: true 
    });

    // 3. Formatting Date
    c.todayDate = now.toLocaleDateString([], { 
      weekday: 'long', 
      month: 'short', 
      day: 'numeric' 
    });
  }

  updateClock();
  $interval(updateClock, 30000); 
};

