sp_widget.script:
(function () {
    var userGR = new GlideRecord('sys_user');
    userGR.get(gs.getUserID());

    data.user = {
        name: userGR.getDisplayValue('name'),
        title: userGR.getDisplayValue('title')
    };
})();
