sp_widget.script:
(function () {

  data.courses = [];
  var currentUser = gs.getUserID();

  var courseGR = new GlideRecord('x_1862243_training_training_course_table');
  courseGR.addQuery('instructor', currentUser);
  courseGR.query();

  while (courseGR.next()) {

    var regAgg = new GlideAggregate('x_1862243_training_training_registration_table');
    regAgg.addQuery('course', courseGR.sys_id);
    regAgg.addQuery('registration_status', 'registered');
    regAgg.addAggregate('COUNT');
    regAgg.query();
    regAgg.next();

    data.courses.push({
      sys_id: courseGR.getUniqueValue(),
      name: courseGR.getValue('course_name'),
      status: courseGR.is_active ? 'Active' : 'Inactive',
      available: parseInt(courseGR.getValue('available_seats'), 10) || 0,
      registered: parseInt(regAgg.getAggregate('COUNT'), 10) || 0,
      endDate: courseGR.getDisplayValue('course_end_date')
    });
  }

})();
